{
    "keycloak_id": "qwertyujlvkcjt1233",
    "hardware_dependencies": [
        {
            "device_link": "https://developer.nvidia.com/embedded/jetson-nano-developer-kit",
            "device_text": "NVidia Jetson Nano",
            "mandatory": false
        }
    ],
    "information": {
        "component_name": "AgriObject Detector",
        "component_uuid": "6854f5a7-f0b0-4b29-b3d8-5b061de0dbf3",
        "dashboard": "https://flexigrobots.collab-cloud.eu:8123/dashboard",
        "datamodel": "http://schema.vtt.fi/ld/context",
        "description": "Yolo based machinevision component for detecting objects and their movement on agricultural field. Model has been thought with 100s of hours of footage on tractors and other agricultural machines working simultaneously. Model can identify other machinery and livestock from realtime footage provided for the component. Component has interface to receive h265 encoded live video stream from UAVs. Component is also able to access Orion context broker specified in configuration to create and update object entities with their detected coordinates, heading and velocity information.",
        "multiuser_support": true,
        "multtenancy_support": true,
        "provider": "https://www.vtt.fi",
        "version": "1.0"
    },
    "module_properties": {
        "automatic_product_quality_ctrl": false,
        "handle_heavy_loads": true,
        "high_mental_effort_task": true,
        "lim_human_skills_in_qualityctrl": false,
        "lowskil_manual_operations": false,
        "machine_parameters": false,
        "machine_setup": false,
        "machinery_performance_monitoring": false,
        "manual_task_repetitiveness": false,
        "parameters_setup": false,
        "predictive_maintenance_signal_events": false,
        "robotic_systems_collision": true,
        "stressful_cognitive_qualityctrl_activity": false,
        "stressful_manual_activities": false,
        "system_configuration_definition": false,
        "working_environment_conditions": false
    },
    "public_endpoints": [
        {
            "endpoint": "agriobjects:1935",
            "headers": [
                {}
            ],
            "method": "",
            "payload": "binary stream",
            "protocol": "RTMP"
        },
        {
            "endpoint": "/object",
            "headers": [
                {
                    "key": "fiware-service",
                    "value": "openiot"
                },
                {
                    "key": "X-Auth-Token",
                    "value": "OAuth2 Token"
                }
            ],
            "method": "GET",
            "protocol": "REST"
        }
    ],
    "software_dependencies": [
        {
            "mandatory": true,
            "module_link": "https://opencv.org/",
            "module_text": "OpenCV machine vision library"
        },
        {
            "mandatory": true,
            "module_link": "https://github.com/jkuri/opencv-ffmpeg-rtmp-stream",
            "module_text": "FFMpeg-RTMP-OpenCV link"
        }
    ]
}
